      const now = new Date();
      const today = now.toISOString().slice(0, 10);
      const songkickAPIKey = 'RpuYqxFiPPsJPs5l';
      let songkickUrl = `https://api.songkick.com/api/3.0/metro_areas/${songkickCityId}/calendar.json?min_date=${today}&apikey=${songkickAPIKey}`;
      return songkickUrl;
